import { useEffect, useState, useRef } from "react"
import { useCookies } from 'react-cookie';
import { authenticateUser } from "./authenticateUser";
import { useNavigate } from "react-router-dom";


export function SecureRoute(props){

    const effectRan = useRef(false)

    const [ grantAccess, setgrantAccess] = useState(false)
    const [cookies, setCookie] = useCookies();

    const checkUser = async () => {

        const userInfo = await authenticateUser(cookies.session_id)

        if(userInfo.LoggedIn && !props.roles.includes(userInfo.role)){
            window.location.href="/not-authorized"
        }

        if(userInfo.LoggedIn === props.LoggedIn){
            setgrantAccess(true)
        }
        else{
            window.location.href=props.Redirect
        }


    }


    useEffect(() => {

        if(!effectRan.current){

            checkUser()

            effectRan.current = true
        }
    }, [])


    return grantAccess ? props.Route : null
}
