import Column from './column';
import { COLUMNS } from './constants';

function CustomColumn(options) {
  // @ts-expect-error todo(flow->ts) types does not much
  return Column({
    kind: COLUMNS.CUSTOM,
    ...options
  });
}

export default CustomColumn;